require 'rails_helper'

describe Api::V3::CommentsController, type: :controller do

  before do
    authenticate_user
    @post = create(:post, user_id: @user.id)
    @comment = @post.comments.create(user_id: @user.id, content: "Content")
    @reply = @comment.comments.create(user_id: @user.id, content: "Content")
  end

  describe "Should get comments" do
    it "should get post comments" do
      get :index, params: { post_id: @post.id }, format: :json
      expect(response).to have_http_status(:success)
    end

    it "should get comment replies" do
      get :index, params: { post_id: @post.id, comment_id: @comment.id }, format: :json
      expect(response).to have_http_status(:success)
    end
  end

  describe "Should create comments" do
    it "should create post comment" do
      post :create, params: { post_id: @post.id,
                              comment: { content: "Comment text" } }, format: :json
      expect(response).to have_http_status(:created)
    end

    it "should create comment reply" do
      post :create, params: { comment_id: @comment.id,
                              comment: { content: "Comment text" } }, format: :json
      expect(response).to have_http_status(:created)
    end

    it "should create post comment with file" do
      post :create, params: { post_id: @post.id,
                              comment: { content: "Comment text",
                                         file: "data:image/gif;base64," }
      }, format: :json
      expect(response).to have_http_status(:created)
    end

    it "should create comment reply with file" do
      post :create, params: { comment_id: @comment.id,
                              comment: { content: "Comment text",
                                         file: "data:image/gif;base64," }
      }, format: :json
      expect(response).to have_http_status(:created)
    end
  end

  describe "Should update comments" do
    it "should update post comment" do
      post :update, params: { post_id: @post.id,
                              id: @comment.id,
                              comment: { content: "New content" } }, format: :json
      expect(response).to have_http_status(:ok)
    end

    it "should update comment reply" do
      post :update, params: { comment_id: @comment.id,
                              id: @reply.id,
                              comment: { content: "New content" } }, format: :json
      expect(response).to have_http_status(:ok)
    end
  end

  describe "Should delete comments" do
    it "should delete post comment" do
      delete :destroy, params: { post_id: @post.id,
                                 id: @comment.id }, format: :json
      expect(response).to have_http_status(:ok)
    end

    it "should delete comment reply" do
      delete :destroy, params: { comment_id: @comment.id,
                                 id: @reply.id }, format: :json
      expect(response).to have_http_status(:ok)
    end
  end
end